mapper.select:
SELECT ssa.*, ssav.id vid, ssav.`sale_attr_value_name`, IF(sale_attr_value_id IS NULL,"0","1") AS is_checked
FROM spu_sale_attr ssa LEFT JOIN spu_sale_attr_value ssav ON ssa.`spu_id`=ssav.`spu_id` AND ssa.`base_sale_attr_id`=ssav.`base_sale_attr_id` LEFT JOIN `sku_sale_attr_value` skuv ON ssav.`id`= skuv.sale_attr_value_id AND skuv.sku_id=#{skuId}
WHERE ssa.`spu_id`=#{spuId} ORDER BY ssa.base_sale_attr_id,ssav.`id`
